          <span className="text-sm mb-2 group-hover:text-healing-green transition-colors">Descoperă Mai Mult</span>
          <ChevronDown className="text-2xl group-hover:text-healing-green group-hover:scale-110 transition-all" />
        </button>
      </div>

    </section>
  );
}

